import { useRef, useState } from 'react';

import classes from './Checkout.module.css';

const isEmpty = value => value.trim() === '';
const isSixChars = value => value.trim().length == 6;
const isEmail = value => value.includes('@') && value.trim().length < 6;

const Checkout = (props) => {
    const [formInputsValidity, setFormInputsValidity] = useState({
        name: true,
        street: true,
        postalCode: true,
        city: true,
        email: true,
        phone: true
    });


    const nameInputRef = useRef();
    const streetInputRef = useRef();
    const postalCodeInputRef = useRef();
    const cityInputRef = useRef();
    const emailInputRef = useRef();
    const phoneInputRef = useRef();

    const confirmHandler = (event) => {
        event.preventDefault();

        const enteredName = nameInputRef.current.value;
        const enteredStreet = nameInputRef.current.value;
        const enteredPostalCode = nameInputRef.current.value;
        const enteredCity = nameInputRef.current.value;
        const enteredEmail = nameInputRef.current.value;
        const enteredPhone = nameInputRef.current.value;

        const enteredNameIsValid = !isEmpty(enteredName);
        const enteredStreetIsValid = !isEmpty(enteredStreet);
        const enteredPostalCodeIsValid = isSixChars(enteredPostalCode);
        const enteredCityIsValid = !isEmpty(enteredCity);
        const enteredEmailIsValid = isEmail(enteredEmail);
        const enteredPhoneIsValid = !isEmpty(enteredPhone);

        setFormInputsValidity({
            name: enteredNameIsValid,
            street: enteredStreetIsValid,
            postalCode: enteredPostalCodeIsValid,
            city: enteredCityIsValid,
            email: enteredEmailIsValid,
            phone: enteredPhoneIsValid
        });

        const formIsValid =
            enteredNameIsValid &&
            enteredStreetIsValid &&
            enteredPostalCodeIsValid &&
            enteredCityIsValid &&
            enteredEmailIsValid &&
            enteredPhoneIsValid;

        if (!formIsValid) {
            return;
        }

        // submit the cart data
    };

    const nameControlClasses = `${classes.control} ${formInputsValidity.name ? '' : classes.invalid}`;

    return (
        <form className={classes.form} onSubmit={confirmHandler}>
            <div className={nameControlClasses}>
                <label htmlFor='name'>Your Name</label>
                <input
                    type='text'
                    id='name'
                    ref={nameInputRef}
                />
                {!formInputsValidity.name && (
                    <p>Please enter a valid name!</p>
                )}
            </div>
            <div className={classes.control}>
                <label htmlFor='street'>Street</label>
                <input
                    type='text'
                    id='street'
                    ref={streetInputRef}
                />
                {!formInputsValidity.street && (
                    <p>Please enter a valid street!</p>
                )}
            </div>
            <div className={classes.control}>
                <label htmlFor='postal'>Postal Code</label>
                <input
                    type='text'
                    id='postal'
                    ref={postalCodeInputRef}
                />
                                {!formInputsValidity.postalCode && (
                <p>Please enter a valid postal code!</p>
                )}
            </div>
            <div className={classes.control}>
                <label htmlFor='city'>City</label>
                <input
                    type='text'
                    id='city'
                    ref={cityInputRef}
                />
                                {!formInputsValidity.city && (
                <p>Please enter a valid city!</p>
                )}
            </div>
            <div className={classes.control}>
                <label htmlFor='email'>Email address</label>
                <input
                    type='email'
                    id='email'
                    ref={emailInputRef}
                />
                                {!formInputsValidity.name && (
                <p>Please enter a valid email address!</p>
                )}
            </div>
            <div className={classes.control}>
                <label htmlFor='phone'>Phone Number</label>
                <input
                    type='tel'
                    id='phone'
                    pattern="[0-9]{2}-[0-9]{2}-[0-9]{2}-[0-9]{2}-[0-9]{2}" required
                    ref={phoneInputRef}
                />
                <small> Format: 06-12-34-56-78</small>
                {!formInputsValidity.phone && (
                <p>Please enter a valid phone number!</p>
                )}
            </div>
            <div className={classes.actions}>
                {/* 
                    We donnot want to submit anything by clicking "Cancel" button,
                    thus add property type='button'
                */}
                <button type='button' onClick={props.onCancel}>
                    Cancel
                </button>
                <button className={classes.submit}>Confirm</button>
            </div>
        </form>
    );
};

export default Checkout;
