import React, { useContext } from "react";
import Modal from "../UI/Modal";
import CartContext from "../../store/cart-context";
import CartItem from "./CartItem";
import useInput from "../../hooks/use-input";

import classes from './Cart.module.css';

const isNotEmpty = (value) => value.trim() !== '';
const isEmail = (value) => value.includes('@') && value.trim().length > 6;

const Cart = props => {
    // User info input logic and validation -----------------------------------------------
    const {
        value: enteredName,
        isValid: enteredNameIsValid,
        hasError: nameInputHasError,
        valueChangeHandler: nameChangeHandler,
        inputBlurHandler: nameBlurHandler,
        reset: resetNameInput
    } = useInput(isNotEmpty);

    const {
        value: enteredEmail,
        isValid: enteredEmailIsValid,
        hasError: emailInputHasError,
        valueChangeHandler: emailChangeHandler,
        inputBlurHandler: emailBlurHandler,
        reset: resetEmailInput
    } = useInput(isEmail);

    const {
        value: enteredPhone,
        isValid: enteredPhoneIsValid,
        hasError: phoneInputHasError,
        valueChangeHandler: phoneChangeHandler,
        inputBlurHandler: phoneBlurHandler,
        reset: resetPhoneInput
    } = useInput(isNotEmpty);

    let formIsValid = false;

    if (enteredNameIsValid && enteredEmailIsValid && enteredPhoneIsValid) {
        formIsValid = true;
    }

    const formSubmissionHandler = (event) => {
        event.preventDefault();

        if (!formIsValid) {
          return;
        }

        console.log('submitted name: ', enteredName);
        console.log('submitted email address: ', enteredEmail);
        console.log('submitted phone number: ', enteredPhone);

        resetNameInput();
        resetEmailInput();
        resetPhoneInput();
      };

    const nameInputClasses = nameInputHasError ? 'form-control invalid' : 'form-control';
    const emailInputClasses = emailInputHasError ? 'form-control invalid' : 'form-control';
    const phoneInputClasses = phoneInputHasError ? 'form-control invalid' : 'form-control';

    // Adding, removing and calculating total amount of items logic --------------------------------
    const cartCtx = useContext(CartContext);

    const totalAmount = `â‚¬${cartCtx.totalAmount.toFixed(2)}`;
    const hadItems = cartCtx.items.length > 0;

    const cartItemRemoveHandler = id => {
        cartCtx.removeItem(id);
    };

    const cartItemAddHandler = item => {
        cartCtx.addItem({ ...item, amount: 1 })
    };

    const cartItems = (
        <ul className={classes['cart-items']}>
            {cartCtx.items.map(item => (
                <CartItem
                    key={item.id}
                    name={item.name}
                    amount={item.amount}
                    price={item.price}
                    onRemove={cartItemRemoveHandler.bind(null, item.id)}
                    onAdd={cartItemAddHandler.bind(null, item)}
                />
            ))}
        </ul>
    );

    return (
        <Modal onClose={props.onClose}>
            {cartItems}
            <div>
                <span className={classes.total}>Total Amount</span>
                <span>{totalAmount}</span>
            </div>
            <form className={classes.actions} onSubmit={formSubmissionHandler}>
                {hadItems && (
                    <div className={classes['control-group']}>
                        <div className={nameInputHasError}>
                            <label>Name</label>
                            <input
                                type='text'
                                id='name'
                                value={enteredName}
                                onChange={nameChangeHandler}
                                onBlur={nameBlurHandler}
                            />
                            {/* <p>Error message</p> */}
                        </div>
                        <div className={classes['form-control']}>
                            <label>Email Address</label>
                            <input
                                type='email'
                                id='email'
                                value={enteredEmail}
                                onChange={emailChangeHandler}
                                onBlur={emailBlurHandler}
                            />
                            {/* <p>Error message</p> */}
                        </div>
                        <div className={classes['form-control']}>
                            <label>Phone Number</label>
                            <input
                                type="tel"
                                id="phone"
                                pattern="[0-9]{2}-[0-9]{2}-[0-9]{2}-[0-9]{2}-[0-9]{2}" required
                                value={enteredPhone}
                                onChange={phoneChangeHandler}
                                onBlur={phoneBlurHandler}
                            />
                            <small>Format: 06-12-34-56-78</small>
                            {/* <p>Error message</p> */}
                        </div>
                    </div>)}
                <div>
                    <button className={classes['button--alt']} onClick={props.onClose}>Close</button>
                    {hadItems && <button className={classes.button}>Order</button>}
                </div>
            </form>
        </Modal>
    );
}

export default Cart;
