import React, { useState, useEffect, useCallback } from "react";
import MealItem from "./MealItem/MealItem";

import Card from "../UI/Card";
import classes from './AvailableMeals.module.css';

const DUMMY_MEALS = [
    {
        id: 'm1',
        name: 'Spicy Ebi furai Miso Ramen',
        description: 'Spicy miso chicken broth ramen with fried shrimps, nori, seaweed, vegetables. spring onions, bamboo and marinated egg',
        price: 15.80,
    },
    {
        id: 'm2',
        name: 'Tan tan Kara-age Wantan Ramen',
        description: 'Spicy sesame miso chiecken broth ramen with fried chicken, wantan, seaweed, vegetables, spring onions, bamboo and marinated egg',
        price: 15.50,
    },
    {
        id: 'm3',
        name: 'Kara-age Wantan Shoyu Ramen',
        description: 'Soy source based broth ramen with fried chicken, wantan, seaweed, vegetables, spring onions, bamboo and marinated egg',
        price: 15.30,
    },
    {
        id: 'm4',
        name: 'Black Garlic Butatama Miso Ramen',
        description: 'Miso chicken broth ramen with thin sliced pork, nori, seaweed, vegetables, spring onions, bamboo and marinated egg',
        price: 15.80,
    },
    {
        id: 'm5',
        name: 'Veggie Tofu Shoyu Ramen',
        description: 'Soy source based broth ramen with tofu, bell pepper, mushrooms, seaweed, vegetables, spring onions, leek, fried onions, bamboo and marinated egg',
        price: 14.00,
    },
    {
        id: 'm6',
        name: 'Gyoza 12pc',
        description: 'Japanese dumplings filled with chicken',
        price: 10.50,
    },
    {
        id: 'm7',
        name: 'Veggie Gyoza 12pc',
        description: 'Japanese dumplings filled with vegetables',
        price: 10.50,
    },
    {
        id: 'm8',
        name: 'Spicy Mala Gyoza 6pc',
        description: 'Fried Japanese dumplings with spicy mala sauce and coriander filled with chicken',
        price: 6.50,
    },
    {
        id: 'm9',
        name: 'Veggie Spicy Mala Gyoza 6pc',
        description: 'Fried Japanese dumplings with spicy mala sauce and coriander filled with vegetables',
        price: 6.50,
    },
    {
        id: 'm10',
        name: 'Wakame Salad',
        description: 'Seaweed salad',
        price: 6.00,
    },
    {
        id: 'm11',
        name: 'Kimuchi',
        description: 'Spicy fermented vegetables',
        price: 4.90,
    },
    {
        id: 'm12',
        name: 'Gohan',
        description: 'Rice',
        price: 2.00,
    },
    {
        id: 'm13',
        name: 'Ebi furai',
        description: 'Fried shrimps (3pc)',
        price: 6.30,
    },
    {
        id: 'm14',
        name: 'Takoyaki',
        description: 'Octupus balls (4pc)',
        price: 6.30,
    },
];

const AvailableMeals = () => {
    const [meals, setMeals] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchMealsHandler = useCallback(async () => {
        setIsLoading(true);
        setError(null);

        try {
            const response = await fetch('https://food-order-app-22f9a-default-rtdb.europe-west1.firebasedatabase.app/menu.json');

            if (!response.ok) {
                throw new Error('Something went wrong with loading menu');
            }

            const data = await response.json();
            const loadedMenu = [];

            for (const key in data) {
                loadedMenu.push({
                    id: key,
                    name: data[key].name,
                    description: data[key].description,
                    price: data[key].price,
                });
            }
            setMeals(loadedMenu);
        } catch (error) {
            setError(error.message);
        }
        setIsLoading(false);
    }, []);


    useEffect(() => {
        fetchMealsHandler();
    }, [fetchMealsHandler]);


    const mealsList = meals.map(meal => (
        <MealItem
            id={meal.id}
            key={meal.id}
            name={meal.name}
            description={meal.description}
            price={meal.price}
        />
    ));

    return (
        <section className={classes.meals}>
            <Card className={classes['meal-card']}>
                <ul>{mealsList}</ul>
            </Card>
        </section>
    );
};

export default AvailableMeals;
