import React, { useState, useEffect, useCallback } from "react";
import MealItem from "./MealItem/MealItem";

import Card from "../UI/Card";
import classes from './AvailableMeals.module.css';

const AvailableMeals = () => {
    const [meals, setMeals] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchMealsHandler = useCallback(async () => {
        setIsLoading(true);
        setError(null);

        try {
            const response = await fetch('https://food-order-app-22f9a-default-rtdb.europe-west1.firebasedatabase.app/menu.json');

            if (!response.ok) {
                throw new Error('Something went wrong with loading menu');
            }

            const data = await response.json();
            const loadedMenu = [];

            for (const key in data) {
                loadedMenu.push({
                    id: key,
                    name: data[key].name,
                    description: data[key].description,
                    price: data[key].price,
                });
            }
            setMeals(loadedMenu);
        } catch (error) {
            setError(error.message);
        }
        setIsLoading(false);
    }, []);


    useEffect(() => {
        fetchMealsHandler();
    }, [fetchMealsHandler]);


    const mealsList = meals.map(meal => (
        <MealItem
            id={meal.id}
            key={meal.id}
            name={meal.name}
            description={meal.description}
            price={meal.price}
        />
    ));

    let content = <p>There is no meals vailable.</p>

    if (meals.length > 0) {
        content = mealsList;
    }

    if (error) {
        content = <p>{error}</p>
    }

    if (isLoading) {
        content = <p>Loading...</p>;
    }

    return (
        <section className={classes.meals}>
            <Card className={classes['meal-card']}>
                <ul>{content}</ul>
            </Card>
        </section>
    );
};

export default AvailableMeals;
